(120,84,10,2,0,'194.199.224.109',1383550697,0,1,1,1,1,'','Script pour verifier ci une commande s\'execute correctement','#!/bin/sh\n#./root/variables\nset -x\n\nrsync -avr /root/test1 /root/test2\nsortsync=$?\necho $sortsync\nls /root/scripts-news/\nsortmount=$?\necho $sortmount\n\n#set sortsync sortmount\n\necho $sortsync, $sortmount\n\nif\n        [ $sortsync = 0 ]  &amp;&amp; [ $sortmount = 0 ]\n\nthen\n        echo &quot;ok&quot;\n\nelif\n        [ $sortsync != 0 ] &amp;&amp; [ $sortmount != 0 ]\nthen\n        echo &quot;les deux ne marchent pas &quot;\n\nelif\n        [ $sortsync != 0 ]\n\nthen\n        echo &quot;sortsync ne marche pas&quot;\n\nelif\n        [ $sortmount != 0 ]\nthen\n\n        echo &quot; sortmount ne marche pas&quot;\n\nelif\n        [ $sortsync != 0 ] &amp;&amp; [ $sortmount = 0 ]\nthen\n        echo &quot;les deux ne marchent pas &quot;\n\nfi\n\nexit 1','c4370095b81a21f445c0b6e76000f33a',0,'','3fzzdpnr',1,0,'',0,0,0,1,0,'',0),
