(202,132,102,2,0,'194.199.224.109',1383565667,0,1,1,1,1,'','INPUT','@echo off\n\n\nset /p choix=Que voulez vous faire ?\n\nif /I &quot;%choix%&quot;==&quot;1&quot; (goto :A)\nif /I &quot;%choix%&quot;==&quot;2&quot; (goto :B)\nif /I &quot;%choix%&quot;==&quot;3&quot; (goto :C)\ngoto question\n\n:A\n\n:B\n:C\n\n\n\n\n\n\npause','2ae048862805f4f0cf70042c41dd9402',0,'','2ox0rtuc',1,0,'',0,0,0,1,0,'',0),
(203,133,102,2,0,'194.199.224.109',1383565690,0,1,1,1,1,'','Install AUTO','@echo off\ncd &quot;Logiciel\\Pack APPLI&quot;\nSTART /WAIT REGEDIT /S &quot;disable security 00.reg&quot;\n\n\n\n\n:run\ncls\necho             ******************************************************\necho             *            Installation automatique                *\necho             ******************************************************\necho             /                                                    /\necho             /              veuillez patientez SVP ...            /\necho             /                                                    /\necho             /----------------------------------------------------/\necho.\n\necho 1) Verication de la presence des logiciels\necho 2) Installation du pack de logiciels\necho.\necho --------------------------------------------\necho.\necho 3) Installation de PACK OFFICE + PDF\necho 4) Installation de Adobe reader\necho 5) Installation de Flash player ( ie + Firefox )\necho 6) Installation de VLC\necho 7) Installation de Firefox\necho 8) Installation de JAVA\necho 9) Installation de OPEN OFFICE\necho q) Quiter\n\n\n\nset /p choix=Que voulez vous faire ?\n\nif /I &quot;%choix%&quot;==&quot;1&quot; (goto :1VERIF)\nif /I &quot;%choix%&quot;==&quot;2&quot; (goto :2INSTALLAUTO)\nif /I &quot;%choix%&quot;==&quot;3&quot; (goto :3OFFICE)\nif /I &quot;%choix%&quot;==&quot;4&quot; (goto :4PDF)\nif /I &quot;%choix%&quot;==&quot;5&quot; (goto :5FLASH)\nif /I &quot;%choix%&quot;==&quot;6&quot; (goto :6VLC)\nif /I &quot;%choix%&quot;==&quot;7&quot; (goto :7FIREFOX)\nif /I &quot;%choix%&quot;==&quot;8&quot; (goto :8JAVA)\nif /I &quot;%choix%&quot;==&quot;9&quot; (goto :9OPEN)\nif /I &quot;%choix%&quot;==&quot;q&quot; (goto :exit)\nif /I &quot;%choix%&quot;==&quot;Q&quot; (goto :exit)\n\n\n:1VERIF\n            cls\n			echo             ******************************************************\n			echo             *            Installation automatique     