(925,686,202,2,0,'195.200.169.110',1560926644,0,1,1,1,1,'','Configuration FDIS','<t>cls<br/>\n<br/>\n# PARAMETERS<br/>\n$account = \"Admin\" # target account for password change<br/>\n$csvfile = \"$PSScriptRoot\\listing.csv\" # full path to csv file with target values<br/>\n# END PARAM<br/>\n<br/>\n<br/>\n<br/>\n<br/>\nfunction ExitWithCode<br/>\n{<br/>\n    param ($exitcode)<br/>\n    $host.SetShouldExit($exitcode)<br/>\n    exit<br/>\n} <br/>\n<br/>\n<br/>\n<br/>\n<br/>\n# get serial number in BIOS<br/>\n$sn = (Get-WmiObject -Class Win32_BIOS).serialnumber<br/>\nif ($sn.Length -eq 0) {<br/>\n    Write-Host \"Numero de serie vide ! Arret du script\" -BackgroundColor DarkRed<br/>\n    Pause<br/>\n    ExitWithCode 1<br/>\n}<br/>\n<br/>\n# verify CSV presence<br/>\nif (!(Test-Path -LiteralPath $csvfile)) {<br/>\n    Write-Host \"Fichier listing manquant ! Arret du script\" -BackgroundColor DarkRed<br/>\n    Pause<br/>\n    ExitWithCode 2<br/>\n}<br/>\n<br/>\n# import &amp; filter CSV<br/>\n$listing= Import-Csv -LiteralPath $csvfile -Delimiter \",\"<br/>\nif ($listing.Count -eq 0) {<br/>\n    Write-Host \"Listing vide ! Arret du script\" -BackgroundColor darkred<br/>\n    Pause<br/>\n    ExitWithCode 3<br/>\n}<br/>\n$values = $listing | ? {$_.serial -like $sn}<br/>\nif ($values.count -eq 0) {<br/>\n    Write-Host \"Machine non repertoriee dans le listing ! Arret du script\" -BackgroundColor DarkRed<br/>\n    Pause<br/>\n    ExitWithCode 4<br/>\n}<br/>\n<br/>\n# rename computer<br/>\nWrite-Host \"`nNouveau nom d\'hôte : $($values.hostname)\" -BackgroundColor Black<br/>\nRename-Computer -NewName $values.hostname -Force<br/>\nif (!$?) {<br/>\n    Write-Host \"Erreur lors du changement de nom d\'hote ! Arret du script\" -BackgroundColor DarkRed<br/>\n    ExitWithCode 5<br/>\n}<br/>\nelse {<br/>\n    Write-Host \"Changement du nom d\'hote reussi.\" -BackgroundColor DarkGreen<br/>\n}<br/>\n<br/>\n# verify target account presence<br/>\n$accounts = Get-LocalUser<br/>\nif (!($accounts | ? {$_.name -like $account})) {<br/>\n    Write-Host \"Le compte $account n\'existe pas ! Arret du script\" -BackgroundColor DarkRed<br/>\n    Pause<br/>\n    ExitWithCode 6<br/>\n}<br/>\n<br/>\n# set target account password<br/>\nWrite-Host \"`nNouveau mot de passe pour le compte $account : $($values.password)\" -BackgroundColor Black<br/>\n$Password= ConvertTo-SecureString $values.password -AsPlainText -Force<br/>\nSet-LocalUser -Name $account -Password $Password<br/>\nif (!$?) {<br/>\n    Write-Host \"Erreur lors du changement de mot de passe ! Arret du script\" -BackgroundColor DarkRed<br/>\n    ExitWithCode 7<br/>\n}<br/>\nelse {<br/>\n    Write-Host \"Changement de mode de passe reussi.\" -BackgroundColor DarkGreen<br/>\n}<br/>\n<br/>\n<br/>\nWrite-Host \"\"<br/>\nWrite-Host \"Fin du script.\" -BackgroundColor DarkGreen<br/>\n#Pause<br/>\n<br/>\nExitWithCode 0</t>','f7fcbaaa5e106e1be5d225010f15f9f0',0,'','2mqkzppr',1,0,'',0,0,0,1,0,'',0),
