(937,696,201,2,0,'195.200.169.110',1563177418,0,1,1,1,1,'','Créer des variables en PowerShell sous forme de tableau','<r><QUOTE><s>[quote]</s>#Config<br/>\n<br/>\n$csvfile = \".\\Name.csv\"<br/>\n<br/>\n# Créatuin d\'une fonction sur le quelle on va faire appel pour ajouter des utilisateurs dans le tableau<br/>\nfunction addUser<br/>\n{<br/>\n param([string]$Nom,[string]$Prenom,[string]$Mail)<br/>\n $d=New-Object PSObject<br/>\n $d | Add-Member -Name Nom -MemberType NoteProperty -Value $Nom<br/>\n $d | Add-Member -Name Prenom -MemberType NoteProperty -Value $Prenom<br/>\n $d | Add-Member -Name Mail -MemberType NoteProperty -Value $Mail<br/>\n return $d<br/>\n}<br/>\n$MaListe=@()<br/>\n$MaListe+=addUser -Nom \"Mergoux\" -Prenom \"Gregory\" -Mail \"<EMAIL email=\"addr1@gmail.com\">addr1@gmail.com</EMAIL>\"<br/>\n$MaListe+=addUser -Nom \"Denis\" -Prenom \"Michel\" -Mail \"<EMAIL email=\"addr2@gmail.com\">addr2@gmail.com</EMAIL>\"<br/>\n$MaListe+=addUser -Nom \"Dupond\" -Prenom \"Lionel\" -Mail \"<EMAIL email=\"addr3@gmail.com\">addr3@gmail.com</EMAIL>\"<br/>\n$MaListe+=addUser -Nom \"Ballais\" -Prenom \"Monique\" -Mail \"<EMAIL email=\"addr4@gmail.com\">addr4@gmail.com</EMAIL>\"<br/>\n$MaListe+=addUser -Nom \"Chirac\" -Prenom \"Brigitte\" -Mail \"<EMAIL email=\"addr5@gmail.com\">addr5@gmail.com</EMAIL>\"<br/>\n$MaListe+=addUser -Nom \"Martin\" -Prenom \"Etienne\" -Mail \"<EMAIL email=\"addr6@gmail.com\">addr6@gmail.com</EMAIL>\"<br/>\n$MaListe+=addUser -Nom \"Dupont\" -Prenom \"Monique\" -Mail \"<EMAIL email=\"addr7@gmail.com\">addr7@gmail.com</EMAIL>\"<br/>\n<br/>\n# Extraire le tableau dans un fichier CSV<br/>\n$MaListe | ConvertTo-Csv -NoTypeInformation -Delimiter \";\"| % {$_.replace(\'\"\',\'\')} | Out-File $csvfile -Encoding utf8 -Force\n<e>[/quote]</e></QUOTE></r>','f5fd7a1b645172c24063bdefc210dd44',0,'gA==','30d31j6x',1,0,'',0,0,0,1,0,'',0),
