(991,747,202,2,0,'195.200.169.110',1583232222,0,1,1,1,1,'','Rechercher les fichier non utiliser depuis X temps','<t>cls<br/>\n<br/>\n$target = \"srv-fichiers\"<br/>\n$2Years = (get-date).AddYears(-2)<br/>\n<br/>\n<br/>\n$shares = Get-WmiObject -ComputerName $target -Class Win32_Share -ErrorAction Stop | ? {$_.name -notmatch \".*\\$.*\"}<br/>\n<br/>\n$shares | Select-Object -ExpandProperty name | %{<br/>\n    $path = \"\\\\$target\\$_\"<br/>\n<br/>\n    write-host \"`n$path : \"<br/>\n<br/>\n    $tempgci = Get-ChildItem -LiteralPath $path -Recurse -ErrorAction SilentlyContinue<br/>\n    $totalsize = \"{0:N2} GB\" -f (( $tempgci | Measure-Object -Property Length -Sum -ErrorAction Stop).Sum / 1GB)<br/>\n    $oldsize = \"{0:N2} GB\" -f (( $tempgci | ? {$_.LastWriteTime –lt $2Years}| Measure-Object -Property Length -Sum -ErrorAction Stop).Sum / 1GB)<br/>\n<br/>\n    Write-Host \"`tTaille totale : $totalsize\"<br/>\n    Write-Host \"`tTaille fichiers non modifiés depuis + de 2 ans : $oldsize\"<br/>\n    Remove-Variable path, tempgci, totalsize, oldsize -ErrorAction Ignore<br/>\n}<br/>\n<br/>\nRemove-Variable shares -ErrorAction Ignore</t>','7cdb6eaa9f204adec53b58811fa6037b',0,'','27ncoapd',1,0,'',0,0,0,1,0,'',0),
