(1,2,0,10,1494257813,'Filtrage IPTABLE + Ouverture de port','#!/bin/sh \n\n### BEGIN INIT INFO \n# Provides: iptables \n# Required-Start: \n# Should-Start: \n# Required-Stop: \n# Should-Stop: \n# Default-Start: 2 3 4 5 \n# Default-Stop: 0 1 6 \n# Short-description: iptables \n# Description: Firewall \n### END INIT INFO \n\n\n[code]### RAZ \niptables -t filter -F \niptables -t filter -X \niptables -t nat -F[/code]\n\n[code]### FERMETURE TOTALE PAR DEFAUT \niptables -t filter -P INPUT DROP \niptables -t filter -P FORWARD DROP \niptables -t filter -P OUTPUT DROP [/code]\n\n[code]### MAINTIEN DES CONNEXIONS EXISTANTES \niptables -A INPUT -m state --state RELATED,ESTABLISHED -j ACCEPT \niptables -A OUTPUT -m state --state RELATED,ESTABLISHED -j ACCEPT [/code]\n\n### AUTORISATION DU LOOPBACK \niptables -t filter -A INPUT -i lo -j ACCEPT \niptables -t filter -A OUTPUT -o lo -j ACCEPT \n\n[code]### AUTORISATION DU PING \niptables -t filter -A INPUT -p icmp -j ACCEPT \niptables -t filter -A OUTPUT -p icmp -j ACCEPT [/code]\n\n### AUTORISATIONS LIEES AUX SERVICES \n[code]# SSH port 22 \niptables -t filter -A OUTPUT -p tcp --dport 22 -j ACCEPT \niptables -t filter -A INPUT -p tcp --dport 22 -j ACCEPT \n[/code]\n\n[code]# SSH port 3546 \niptables -t filter -A OUTPUT -p tcp --dport 3546 -j ACCEPT \niptables -t filter -A INPUT -p tcp --dport 3546 -j ACCEPT [/code]\n\n[code]# DNS \niptables -t filter -A OUTPUT -p tcp --dport 53 -j ACCEPT \niptables -t filter -A INPUT -p tcp --dport 53 -j ACCEPT \niptables -t filter -A OUTPUT -p udp --dport 53 -j ACCEPT \niptables -t filter -A INPUT -p udp --dport 53 -j ACCEPT [/code]\n\n[code]# HTTP \niptables -t filter -A OUTPUT -p tcp --dport 80 -j ACCEPT \niptables -t filter -A INPUT -p tcp --dport 80 -j ACCEPT [/code]\n\n[code]# HTTPS \niptables -t filter -A OUTPUT -p tcp --dport 443 -j ACCEPT \niptables -t filter -A INPUT -p tcp --dport 443 -j ACCEPT \n[/code]\n\n[code]# FTP \niptables -t filter -A OUTPUT -p tcp --dport 21 -j ACCEPT \niptables -t filter -A INPUT -p tcp --dport 21 -j ACCEPT[/code]\n\n[code]iptables -t filter -A OUTPUT -p tcp --dport 1723 -j ACCEPT\niptables -t filter -A INPUT -p tcp --dport 1723 -j ACCEPT\n[/code]\n# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # \n\n\n[code]\n# SSH port WEBTCV sur l\'adresse ip de la maison \niptables -t filter -A OUTPUT -s 176.130.157.149 -p tcp --dport 32400 -j ACCEPT\niptables -t filter -A INPUT -s 176.130.157.149 -p tcp --dport 32400 -j ACCEPT\n[/code]\n\n[code]# SSH port Torrent sur l\'adresse ip de la maison\niptables -t filter -A OUTPUT -s 176.130.157.149 -p tcp --dport 9091 -j ACCEPT\niptables -t filter -A INPUT -s 176.130.157.149 -p tcp --dport 9091 -j ACCEPT\n[/code]\n\niptables -t filter -A OUTPUT -s 176.130.157.149 -p tcp --dport 3000 -j ACCEPT\niptables -t filter -A INPUT -s 176.130.157.149 -p tcp --dport 3000 -j ACCEPT\n\n[code]# SSH port Torrent sur l\'adresse ip du t√©l√©phone\niptables -t filter -A OUTPUT -s 80.214.120.224 -p tcp --dport 9091 -j ACCEPT\niptables -t filter -A INPUT -s 80.214.120.224 -p tcp --dport 9091 -j ACCEPT[/code]'),
(2,2,0,188,1675027202,'Connection √† une base de donn√©e MYSQL','&lt;?php\n//Connexion √† la base de donn√©es MySQL\n$servername = &quot;localhost&quot;;\n$username = &quot;root&quot;;\n$password = &quot;&quot;;\n$dbname = &quot;ma base&quot;;\n\n// Cr√©ez une nouvelle connexion\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// V√©rifiez la connexion\nif ($conn-&gt;connect_error) {\n    die(&quot;Connection failed: &quot; . $conn-&gt;connect_error);\n} \n\n// R√©cup√©rez les champs de la table &quot;ma table&quot;\n$sql = &quot;SELECT champs1, champs2, champs3 FROM ma table&quot;;\n$result = $conn-&gt;query($sql);\n\n// Affichez les donn√©es de la table\nif ($result-&gt;num_rows &gt; 0) {\n    // Sortez chaque ligne\n    while($row = $result-&gt;fetch_assoc()) {\n        echo &quot;Champs1: &quot; . $row[&quot;champs1&quot;]. &quot;, Champs2: &quot; . $row[&quot;champs2&quot;]. &quot;, Champs3: &quot; . $row[&quot;champs3&quot;]. &quot;&lt;br&gt;&quot;;\n    }\n} else {\n    echo &quot;0 results&quot;;\n}\n$conn-&gt;close();\n?&gt;');
