(10,7,13,2,0,'194.199.224.109',1383544409,0,1,1,1,1,'','/root/.bashrc','[quote:1byalw5b]# ~/.bashrc: executed by bash(1) for non-login shells.\n\n#export PS1=&quot;\\[\\033[1;31m\\]\\u\\[\\033[1;37m\\]@\\[\\033[1;33m\\]\\h\\[\\033[1;31m\\]:\\w\\$&quot;\nexport PS1=&quot;\\[\\033[1;31m\\]\\u\\[\\033[1;37m\\]@\\[\\033[1;33m\\]\\h\\[\\033[0m\\]:\\w\\$&quot;\numask 022\n\n\n\n\n# You may uncomment the following lines if you want `ls\' to be colorized:\n\n\n export LS_OPTIONS=\'--color=auto\'\n eval &quot;`dircolors`&quot;\n alias ls=\'ls $LS_OPTIONS\'\n alias ll=\'ls $LS_OPTIONS -lah\'\n alias l=\'ls $LS_OPTIONS -lA\'\n #alias sss=\'ssh 192.168.3.111\'\n alias df=\'df -h\'\n alias sss=\'ssh 192.168.3.15:222\'\n#\n# Some more alias to avoid making mistakes:\n# alias rm=\'rm -i\'\n# alias cp=\'cp -i\'\n# alias mv=\'mv -i\'\nalias cls=clear\nalias md=mkdir\n\n\n\n#######&quot;##############################################################&quot;#############################################################################&quot;\nclear\n\necho &quot;\n			##################################\n			#	   Data-Center		 #\n			##################################\n\n\n&quot;;\ndf -h\necho &quot;\n\n&quot;\nuptime\necho &quot;\n&quot;\n\n\n############################################################################&quot;############################################################################&quot;\na=$(df -h | grep /mnt/2To | awk {\'print $5\'} | sed \'s/\\%//g\')\n#echo $a\n\nif   [ $a -lt 90 ]; then\n\n        echo &quot;&quot;\n\n\nelse\n\n	RED=`tput setaf 1` \n        echo &quot;${RED}ATTENTION : il ny a presque plu de place sur le disque dur \n&quot;\n\nfi\n\n\n			############################################################################&quot;\n\n\n\n\nb=$(df -h | grep &quot;/&quot; | awk {\'print $5\'} | sed \'s/\\%//g\');\nq=$(echo ${b:0:2});\n\n\n\nif   [ $q -lt 90 ]; then\n\n        echo &quot;&quot;\n\n\nelse\n\n        RED=`tput setaf 1`\n        echo &quot;${RED}ATTENTION