(14,10,12,2,0,'194.199.224.109',1383544708,0,1,1,1,1,'','proxy.pac','[code:2igk5buo]function FindProxyForURL(url, host) \n{ \nif ( \n(isPlainHostName(host)) || \n(dnsDomainIs(host, &quot;&#46;Antec-Server&quot;)) || \n(isInNet(host, &quot;10&#46;0&#46;0&#46;0&quot;, &quot;255&#46;0&#46;0&#46;0&quot;)) || \n(isInNet(host, &quot;172&#46;16&#46;0&#46;0&quot;, &quot;255&#46;240&#46;0&#46;0&quot;)) || \n(isInNet(host, &quot;169&#46;254&#46;0&#46;0&quot;, &quot;255&#46;255&#46;0&#46;0&quot;)) || \n(isInNet(host, &quot;192&#46;168&#46;0&#46;0&quot;, &quot;255&#46;255&#46;0&#46;0&quot;)) \n) \nreturn &quot;DIRECT&quot;; \n\nelse \n\nif ( \n(isInNet(myIpAddress(), &quot;192&#46;168&#46;1&#46;0&quot;, &quot;255&#46;255&#46;255&#46;0&quot;)) \n) \nreturn &quot;PROXY 192&#46;168&#46;1&#46;254&#58;800&quot;; \n} \n \nRevenir en haut de page        \n \n[/code:2igk5buo]','3dffa9b7df97175729805e0f7432bad2',0,'AIA=','2igk5buo',1,0,'',0,0,0,1,0,'',0),
(15,11,12,2,0,'194.199.224.109',1383544750,0,1,1,1,1,'','httpd.conf (pour le protocoles WPAD)','[quote:4od433v4]## \n## httpd.conf -- Apache HTTP server configuration file \n## \n## $Id: httpd.conf,v 1.15.2.7 2005/04/16 11:40:15 rkerr Exp $ \n## \nServerType standalone \nServerRoot /etc/httpd \n\nLockFile /var/lock/httpd.lock \nPidFile /var/run/httpd.pid \nScoreBoardFile /var/run/httpd.scoreboard \nTimeout 900 \nKeepAlive On \nMaxKeepAliveRequests 100 \nKeepAliveTimeout 15 \nMinSpareServers 1 \nMaxSpareServers 2 \nStartServers 2 \nMaxClients 10 \nMaxRequestsPerChild 100 \nPort 81 \nPort 80 \nListen 81 \nListen 80 \nListen 445 \nUser nobody \nGroup nobody \nServerAdmin root@localhost \nServerTokens Prod \nDocumentRoot /home/httpd/html \n# Limit track/trace requests \nRewriteEngine on \nRewriteCond %{REQUEST_METHOD} ^(TRACE|TRACK|OPTIONS) \nRewriteRule .* - [F] \n\n&lt;Directory /&gt; \nOptions None \nAllowOverride None \n&lt;/Directory&gt; \n&lt;Directory /home/httpd/html&gt; \nOptions ExecCGI \nAllowOverride None \nOrder allow,deny \nAllow from all \n&lt;/Directory&gt; \n&lt;DirectoryMatch &quot;/home/httpd/html/(graphs|sgraph)&quot;&gt; \nAuthName &quot;Restricted&quot; \nAuthType Basic \nAuthUserFile /var/ipcop/auth/users \nrequire user admin \n&lt;/DirectoryMatch&gt; \nScriptAlias /cgi-bin/ /home/httpd/cgi-bin/ \n&lt;Directory /home/httpd/cgi-bin&gt; \nAllowOverride None \nOptions None \nAuthName &quot;Restricted&quot; \nAuthType Basic \nAuthUserFile /var/ipcop/auth/users \nRequire user admin \n&lt;Files index.cgi&gt; \nSatisfy Any \nAllow from All \n&lt;/Files&gt; \n# Start of modification by advproxy \n&lt;Files chpasswd.cgi&gt; \nSatisfy Any \nAllow from All \n&lt;/Files&gt; \n&lt;Files webaccess.cgi&gt; \nSatisfy Any \nAllow from All \n&lt;/Files&gt; \n# End of modification by advproxy \n&lt;Files credits.cgi&gt; \nSatisfy Any \nAllow from All \n&lt;/Files&gt; \n&lt;Files dial.cgi&gt; \nRequire user admin dial \n&lt;/Files&gt; \n&lt;/Directory&gt; \n&lt;IfModule mod_dir.c&gt; \nDirectoryIndex index.html index.htm index.shtml index.cgi \n&lt;/IfModule&gt; \nAccessFileName .htaccess \n&lt;Files ~ &quot;^\\.ht&quot;&gt; \nOrder allow,deny \nDeny from all \n&lt;/Files&gt; \n&lt;IfModule mod_mime.c&gt; \nTypesConfig /etc/mime.types \n&lt;/IfModule&gt; \nDefaultType text/plain \n\nHostnameLookups Off \nErrorLog /var/log/httpd/error_log \nLogLevel warn \nLogFormat &quot;%h %l %u %t \\&quot;%r\\&quot; %&gt;s %b&quot; common \nCustomLog /var/log/httpd/access_log common \nServerSignature Off \nAddHandler cgi-script .cgi \n&lt;IfModule mod_setenvif.c&gt; \nBrowserMatch &quot;Mozilla/2&quot; nokeepalive \nBrowserMatch &quot;MSIE 4\\.0b2;&quot; nokeepalive downgrade-1.0 force-response-1.0 \nBrowserMatch &quot;RealPlayer 4\\.0&quot; force-response-1.0 \nBrowserMatch &quot;Java/1\\.0&quot; force-response-1.0 \nBrowserMatch &quot;JDK/1\\.0&quot; force-response-1.0 \n&lt;/IfModule&gt; \n\n### \n### SSL Configuration \n### \nAddType application/x-x509-ca-cert .crt \nAddType application/x-pkcs7-crl .crl \n\nSSLPassPhraseDialog builtin \nSSLSessionCache dbm:/var/log/httpd/ssl_scache \nSSLSessionCacheTimeout 900 \nSSLMutex file:/var/log/httpd/ssl_mutex \nSSLRandomSeed startup builtin \nSSLRandomSeed connect builtin \nSSLLog /var/log/httpd/ssl_engine_log \nSSLLogLevel info \n\n&lt;VirtualHost _default_:445&gt; \nRewriteEngine on \nRewriteCond %{REQUEST_METHOD} ^(TRACE|TRACK|OPTIONS) \nRewriteRule .* - [F] \nDocumentRoot /home/httpd/html \nServerAdmin root@localhost \nErrorLog /var/log/httpd/error_log \nTransferLog /var/log/httpd/access_log \nSSLEngine on \nSSLProtocol all -SSLv2 \nSSLCipherSuite ALL:!ADH:!EXPORT56:!eNULL:!SSLv2:RC4+RSA:+HIGH:+MEDIUM:+LOW:+EXP \nSSLCertificateFile /etc/httpd/server.crt \nSSLCertificateKeyFile /etc/httpd/server.key \n&lt;Files ~ &quot;\\.(cgi|shtml?)$&quot;&gt; \nSSLOptions +StdEnvVars \n&lt;/Files&gt; \n&lt;Directory /home/httpd/cgi-bin&gt; \nSSLOptions +StdEnvVars \n&lt;/Directory&gt; \nSetEnv HOME /home/nobody \nSetEnvIf User-Agent &quot;.*MSIE.*&quot; \\ \nnokeepalive ssl-unclean-shutdown \\ \ndowngrade-1.0 force-response-1.0 \nCustomLog /var/log/httpd/ssl_request_log \\ \n&quot;%t %h %{SSL_PROTOCOL}x %{SSL_CIPHER}x \\&quot;%r\\&quot; %b&quot; \n&lt;/VirtualHost&gt; \n\n&lt;Directory /home/httpd/html/backup&gt; \nOptions None \nAllowOverride None \nAuthName &quot;Restricted&quot; \nAuthType Basic \nAuthUserFile /var/ipcop/auth/users \nrequire user admin \n&lt;/Directory&gt; \n\ninclude /etc/httpd/conf/hostname.conf[/quote:4od433v4]','365b70782e81942148dde979277e5031',0,'gA==','4od433v4',1,0,'',0,0,0,1,0,'',0),
