(319,212,224,2,0,'176.181.139.237',1390603814,0,1,1,1,1,'','Re: IPTABLE','[quote:2yn4svoo]#!/bin/sh\n# Start/stop/restart/status firewall:\n\nfirewall_start(){\n    echo Setting firewall rules...\n\n    ###### Debut Initialisation ######\n\n    # On efface toutes les lignes pr√©c√©dentes\n    iptables -t filter -F\n    iptables -t filter -X\n    echo - Vidage : [OK]\n\n    # --------------------------------------------\n    # Interdire toute connexion entrante\n    # --------------------------------------------\n    iptables -t filter -P INPUT DROP\n    iptables -t filter -P FORWARD DROP\n    echo - Interdire toute connexion entrante : [OK]\n\n    # --------------------------------------------\n    # Interdire toute connexion sortante\n    # --------------------------------------------\n    iptables -t filter -P OUTPUT DROP\n    echo - Interdire toute connexion sortante : [OK]\n\n    # Vider les tables actuelles\n    iptables -t filter -F\n    iptables -t filter -X\n    echo - Vidage : [OK]\n\n    # --------------------------------------------\n    # Creation d\'une chaine\n    # --------------------------------------------\n    iptables -N LOG_REJECT_SMTP\n    iptables -A LOG_REJECT_SMTP -j LOG --log-prefix \' SMTP REJECT PAQUET : \'\n    iptables -A LOG_REJECT_SMTP -j DROP\n\n    # --------------------------------------------\n    # Anti-Taiwanais\n    # --------------------------------------------\n    iptables -t filter -A INPUT -i eth0 -s 61.64.128.0/17 -j LOG_REJECT_SMTP\n    iptables -t filter -A INPUT -i eth0 -s 122.120.0.0/13 -j LOG_REJECT_SMTP\n    iptables -t filter -A INPUT -i eth0 -s 168.95.0.0/16 -j LOG_REJECT_SMTP\n    echo - Bloquer Taiwanais : [OK]\n\n    # --------------------------------------------\n    # Ne pas casser les connexions etablies\n    # --------------------------------------------\n    iptables -A INPUT -m state --state RELATED,ESTABLISHED -j ACCEPT\n    iptables -A OUTPUT -m state --state RELATED,ESTABLISHED -j ACCEPT\n    echo - Ne pas casser les connexions √©tablies : [OK]\n\n     # --------------------------------------------\n    # Autoriser la Supervision du serveur (SNMP)\n    # --------------------------------------------\n    iptables -t filter -A INPUT -p tcp --dport 161 -s 94.xx.xxx.xxx/32 -j ACCEPT\n    iptables -t filter -A INPUT -p udp --dport 161 -s 94.xx.xxx.xxx/32 -j ACCEPT\n    echo - Autoriser Supervision : [OK]\n\n    # --------------------------------------------\n    # Autoriser les requetes DNS, FTP, HTTP, NTP\n    # --------------------------------------------\n    iptables -t filter -A OUTPUT -p tcp --dport 21 -j ACCEPT\n    iptables -t filter -A OUTPUT -p tcp --dport 80 -j ACCEPT\n    iptables -t filter -A OUTPUT -p tcp --dport 53 -j ACCEPT\n    iptables -t filter -A OUTPUT -p udp --dport 53 -j ACCEPT\n    iptables -t filter -A OUTPUT -p udp --dport 123 -j ACCEPT\n    echo - Autoriser les requetes DNS, FTP, HTTP : [OK]\n\n    # --------------------------------------------\n    # Autoriser loopback\n    # --------------------------------------------\n    iptables -t filter -A INPUT -i lo -j ACCEPT\n    iptables -t filter -A OUTPUT -o lo -j ACCEPT\n    echo - Autoriser loopback : [OK]\n\n    # --------------------------------------------\n    # Autoriser ping\n    # --------------------------------------------\n    iptables -t filter -A INPUT -p icmp -j ACCEPT\n    iptables -t filter -A OUTPUT -p icmp -j ACCEPT\n    echo - Autoriser ping : [OK]\n\n    # --------------------------------------------\n    # HTTP\n    # --------------------------------------------\n    iptables -t filter -A INPUT -p tcp --dport 80 -j ACCEPT\n    iptables -t filter -A INPUT -p tcp --dport 443 -j ACCEPT\n    echo - Autoriser serveur Apache : [OK]\n\n    # --------------------------------------------\n    # FTP\n    # --------------------------------------------\n    iptables -t filter -A INPUT -p tcp --dport 20 -j ACCEPT\n    iptables -t filter -A INPUT -p tcp --dport xxxxx -j ACCEPT\n    iptables -A INPUT -p tcp --dport xxxxx:xxxxx -j ACCEPT\n    iptables -t filter -A INPUT -m state --state ESTABLISHED,RELATED -j ACCEPT\n    echo - Autoriser serveur FTP : [OK]\n\n    # --------------------------------------------\n    # DNS\n    # --------------------------------------------\n    iptables -t filter -A INPUT -p tcp --dport 53 -j ACCEPT\n    iptables -t filter -A INPUT -p udp --dport 53 -j ACCEPT\n    echo - Autoriser serveur Bind : [OK]\n\n    # --------------------------------------------\n    # MAIL\n    # --------------------------------------------\n    iptables -t filter -A INPUT -p tcp --dport 25 -j ACCEPT\n    iptables -t filter -A INPUT -p tcp --dport 110 -j ACCEPT\n    iptables -t filter -A INPUT -p tcp --dport 143 -j ACCEPT\n    iptables -t filter -A OUTPUT -p tcp --dport 25 -j ACCEPT\n    iptables -t filter -A OUTPUT -p tcp --dport 110 -j ACCEPT\n    iptables -t filter -A OUTPUT -p tcp --dport 143 -j ACCEPT\n    echo - Autoriser serveur Mail : [OK]\n\n    # --------------------------------------------\n    # Autoriser SSH\n    # --------------------------------------------\n    iptables -t filter -A INPUT -p tcp --dport XXX-XXX -m recent --rcheck --seconds 60 --hitcount 2 --name SSH -j LOG --log-prefix &quot;SSH REJECT&quot;\n    iptables -t filter -A INPUT -p tcp --dport XXX-XXX -m recent --update --seconds 60 --hitcount 2 --name SSH -j DROP\n    iptables -t filter -A INPUT -p tcp --dport XXX-XXX -m state --state NEW -m recent --set --name SSH -j ACCEPT\n    echo - Autoriser SSH : [OK]\n\n    # -------------------------------------------\n    # RTM OVH\n    # -------------------------------------------\n    iptables -t filter -A OUTPUT -p udp --dport 6100:6200 -j ACCEPT\n    echo - Autoriser RTM OVH : [OK]\n}\n\nfirewall_stop(){\n    iptables -F\n    iptables -X\n    iptables -P INPUT ACCEPT\n    iptables -P FORWARD ACCEPT\n    iptables -P OUTPUT ACCEPT\n\n    iptables -t nat -F\n    iptables -t nat -X\n    iptables -t nat -P PREROUTING ACCEPT\n    iptables -t nat -P POSTROUTING ACCEPT\n    iptables -t nat -P OUTPUT ACCEPT\n\n    iptables -t mangle -F\n    iptables -t mangle -X\n    iptables -t mangle -P PREROUTING ACCEPT\n    iptables -t mangle -P OUTPUT ACCEPT\n\n    echo &quot; [Firewall arr√™t√© !] &quot;\n}\n\nfirewall_restart(){\n    firewall_stop\n    sleep 2\n    firewall_start\n}\n\n# /etc/init.d/firewall options\n\ncase $1 in\n\'start\' )\nfirewall_start\n\'start\' )\nfirewall_start\n;;\n\'stop\' )\nfirewall_stop\n;;\n\'restart\' )\nfirewall_stop\nfirewall_start\n;;\n\'status\' )\n[ $2 ] &amp;&amp; iptables -L -v -n -t $2 || iptables -L -v -n\n;;\n*)\necho &quot;usage: -bash {start|stop|restart|status}&quot;\n;;\nesac[/quote:2yn4svoo]','a9d52b4a98179c84401c0a639954b795',0,'gA==','2yn4svoo',1,0,'',0,0,0,1,0,'',0),
