tbl (id,date_verif,date,pseudo,email,messages) VALUES (\'\',\'$date_verif\',\'$date\',\'$pseudo\',\'$email\',\'$message\') \") or die (\"erreur requ√™te \".mysql_error(√†); <br/>\n<br/>\n//on ferme la connexion<br/>\nmysql_close(); <br/>\n<br/>\n//on redirige le visiteur sur la page d\'accueil, l√† ou s\'afficheront les messages<br/>\nheader(\'location: accueil.php\');<br/>\n<br/>\n}<br/>\n?&gt;<br/>\n  <br/>\n<br/>\n <br/>\nLa page ou s\'afficheront les messages ( accueil.php )<br/>\n<br/>\n&lt;?php<br/>\n//----------------------------------------------------------------<br/>\n//<br/>\n// Page d\'affichage des messages<br/>\n// <br/>\n//----------------------------------------------------------------<br/>\n<br/>\n$mysql_host = \'localhost\'; // entrer votre hote ici<br/>\n$mysql_user = \'votre login\'; // entrer votre login<br/>\n$mysql_password = \'password\'; // entrer votre password<br/>\n$mysql_base = \'le nom de votre base de donn√©e\'; // entrer le nom de votre base<br/>\n<br/>\n//----------------------------------------------------------------<br/>\n<br/>\n//on se connecte √† la base de donn√©e<br/>\n$db = mysql_connect($mysql_host,$mysql_user,$mysql_password) or die (\"erreur de connexion\");<br/>\n<br/>\n//s√©lection de votre base de donn√©e<br/>\nmysql_select_db($mysql_base,$db) or die (\"erreur de connexion<br/>\nbase\");<br/>\n<br/>\n//la requ√™te de s√©lection des messages<br/>\n$req = mysql_query(\"SELECT date,pseudo,email,message from livre_tbl Order by date_verif Desc \") or die (\"erreur requ√™te\");<br/>\n<br/>\n//on ressort le contenu en boucle, et on stock dans un tableau avec mysql_fetch_array<br/>\nwhile( $contenu = mysql_fetch_array ($req))<br/>\n{<br/>\n<br/>\n//on affiche le r√©sultat<br/>\nprint \'Post√© le : \'.$contenu[\'date\'].\'&lt;br / &gt;\';<br/>\nprint \'Auteur : &lt;a href=\"mailto:\'.$contenu[\'email\'].\'\"&gt;\'.$contenu[\'pseudo\'].\'&lt;/a&gt;&lt;br / &gt;\';<br/>\nprint \'Message : &lt;br /&gt;\'.$contenu[message].\'&lt;br / &gt;\';<br/>\nprint \'&lt;hr /&gt;\';<br/>\n<br/>\n}<br/>\n<br/>\n//on referme la connexion<br/>\nmysql_close();<br/>\n<br/>\n//----------------- FIN DU SCRIPT --------------------------------<br/>\n<br/>\n?&gt; <br/>\n <br/>\n<br/>\n <br/>\n<br/>\nEt voil√†, c\'est fini, vous avez un code complet, et la m√©thode pour cr√©er vos scripts, c\'est d√©j√† √ßa de gagner <E>;-)</E> <br/>\n<br/>\nJe pr√©cise quand m√™me pour les petits malins qui se seraient content√© uniquement de copier le code sans faire l\'effort de comprendre un minimum, parce que c\'est mieux quand c\'est tout fait, que celui-ci n\'est pas complet √† 100%, et que si vous souhaitez que le livre d\'or s\'affiche comme il faut , il faudra chercher un peu dans les cours et se creuser les m√©ninges, et oui, dommage <E>;-)</E>)<br/>\n<br/>\n <br/>\nA vos claviers et bonne prog <br/>\n\'e\' Stressman</r>','54f858a55e490c002c1fad6c029dd01f',0,'','gseabopi',1,0,'',0,0,0,1,0,'',0),
