-------------------------------<br/>\n    Private Structure ControlInfo<br/>\n        Public name As String<br/>\n        Public parentName As String<br/>\n        Public leftOffsetPercent As Double<br/>\n        Public topOffsetPercent As Double<br/>\n        Public heightPercent As Double<br/>\n        Public originalHeight As Integer<br/>\n        Public originalWidth As Integer<br/>\n        Public widthPercent As Double<br/>\n        Public originalFontSize As Single<br/>\n    End Structure<br/>\n<br/>\n    \'-------------------------------------------------------------------------<br/>\n    \' ctrlDict<br/>\n    \' Dictionary of (control name, control info) for all processed controls<br/>\n    \'-------------------------------------------------------------------------<br/>\n    Private ctrlDict As Dictionary(Of String, ControlInfo) = New Dictionary(Of String, ControlInfo)<br/>\n<br/>\n    \'----------------------------------------------------------------------------------------<br/>\n    \' FindAllControls<br/>\n    \' Recursive function to process all controls contained in the initially passed<br/>\n    \' control container and store it in the Control dictionary<br/>\n    \'----------------------------------------------------------------------------------------<br/>\n    Public Sub FindAllControls(thisCtrl As Control)<br/>\n<br/>\n        \'-- If the current control has a parent, store all original relative position<br/>\n        \'-- and size information in the dictionary.<br/>\n        \'-- Recursively call FindAllControls for each control contained in the<br/>\n        \'-- current Control<br/>\n        For Each ctl As Control In thisCtrl.Controls<br/>\n            Try<br/>\n                If Not IsNothing(ctl.Parent) Then<br/>\n                    Dim parentHeight = ctl.Parent.Height<br/>\n                    Dim parentWidth = ctl.Parent.Width<br/>\n<br/>\n                    Dim c As New ControlInfo<br/>\n                    c.name = ctl.Name<br/>\n                    c.parentName = ctl.Parent.Name<br/>\n                    c.topOffsetPercent = Convert.ToDouble(ctl.Top) / Convert.ToDouble(parentHeight)<br/>\n                    c.leftOffsetPercent = Convert.ToDouble(ctl.Left) / Convert.ToDouble(parentWidth)<br/>\n                    c.heightPercent = Convert.ToDouble(ctl.Height) / Convert.ToDouble(parentHeight)<br/>\n                    c.widthPercent = Convert.ToDouble(ctl.Width) / Convert.ToDouble(parentWidth)<br/>\n                    c.originalFontSize = ctl.Font.Size<br/>\n                    c.originalHeight = ctl.Height<br/>\n                    c.originalWidth = ctl.Width<br/>\n                    ctrlDict.Add(c.name, c)<br/>\n                End If<br/>\n<br/>\n            Catch ex As Exception<br/>\n                Debug.Print(ex.Message)<br/>\n            End Try<br/>\n<br/>\n            If ctl.Controls.Count &gt; 0 Then<br/>\n                FindAllControls(ctl)<br/>\n            End If<br/>\n<br/>\n        Next \'-- For Each<br/>\n<br/>\n    End Sub<br/>\n<br/>\n    \'----------------------------------------------------------------------------------------<br/>\n    \' ResizeAllControls<br/>\n    \' Recursive function to resize and reposition all controls contained in the Control<br/>\n    \' dictionary<br/>\n    \'----------------------------------------------------------------------------------------<br/>\n    Public Sub ResizeAllControls(thisCtrl As Control)<br/>\n<br/>\n        Dim fontRatioW As Single<br/>\n        Dim fontRatioH As Single<br/>\n        Dim fontRatio As Single<br/>\n        Dim f As Font<br/>\n<br/>\n        \'-- Resize and reposition all controls in the passed control<br/>\n        For Each ctl As Control In thisCtrl.Controls<br/>\n            Try<br/>\n                If Not IsNothing(ctl.Parent) Then<br/>\n                    Dim parentHeight = ctl.Parent.Height<br/>\n                    Dim parentWidth = ctl.Parent.Width<br/>\n<br/>\n                    Dim c As New ControlInfo<br/>\n<br/>\n                    Dim ret As Boolean = False<br/>\n                    Try<br/>\n                        \'-- Get the current control\'s info from the control info dictionary<br/>\n                        ret = ctrlDict.TryGetValue(ctl.Name, c)<br/>\n<br/>\n                        \'-- If found, adjust the current control based on control relative<br/>\n                        \'-- size and position information stored in the dictionary<br/>\n                        If (ret) Then<br/>\n                            \'-- Size<br/>\n                            ctl.Width = Int(parentWidth * c.widthPercent)<br/>\n                            ctl.Height = Int(parentHeight * c.heightPercent)<br/>\n<br/>\n                            \'-- Position<br/>\n                            ctl.Top = Int(parentHeight * c.topOffsetPercent)<br/>\n                            ctl.Left = Int(parentWidth * c.leftOffsetPercent)<br/>\n<br/>\n                            \'-- Font<br/>\n                            f = ctl.Font<br/>\n                            fontRatioW = ctl.Width / c.originalWidth<br/>\n                            fontRatioH = ctl.Height / c.originalHeight<br/>\n                            fontRatio = (fontRatioW + <br/>\n                            fontRatioH) / 2 \'-- average change in control Height and Width<br/>\n                            ctl.Font = New Font(f.FontFamily, <br/>\n                            c.originalFontSize * fontRatio, f.Style)<br/>\n<br/>\n                        End If<br/>\n                    Catch<br/>\n                    End Try<br/>\n                End If<br/>\n            Catch ex As Exception<br/>\n            End Try<br/>\n<br/>\n            \'-- Recursive call for controls contained in the current control<br/>\n            If ctl.Controls.Count &gt; 0 Then<br/>\n                ResizeAllControls(ctl)<br/>\n            End If<br/>\n<br/>\n        Next \'-- For Each<br/>\n    End Sub<br/>\n<br/>\nEnd Class<e>[/quote]</e></QUOTE></r>','7fc90377c96b24434633289e97cb0d94',0,'0IA=','1rjvlbm9',1,0,'',0,0,0,1,0,'',0),
