(1138,860,199,2,0,'82.66.193.192',1673696498,0,1,1,1,1,'','inventaire PC','<r><CODE><s>[code]</s># R√©cup√©rer le nom de l\'ordinateur\n$computerName = (Get-WmiObject -Class Win32_ComputerSystem).Name\n\n# R√©cup√©rer l\'adresse IP\n$ipAddress = (Get-WmiObject -Class Win32_NetworkAdapterConfiguration -Filter \"IPEnabled = \'True\'\").IPAddress[0]\n\n# R√©cup√©rer les logiciels install√©s\n$installedSoftware = Get-WmiObject -Class Win32_Product | Select-Object -Property Name\n\n# R√©cup√©rer les informations sur le processeur\n$cpu = Get-WmiObject -Class Win32_Processor | Select-Object -Property Name, NumberOfCores, NumberOfLogicalProcessors\n\n# R√©cup√©rer les informations sur la m√©moire\n$memory = Get-WmiObject -Class Win32_OperatingSystem | Select-Object -Property TotalVisibleMemorySize, FreePhysicalMemory\n\n# R√©cup√©rer le nom de l\'utilisateur\n$userName = (Get-WmiObject -Class Win32_ComputerSystem).UserName\n\n# Get the used disk space in Go\n$usedSpace = (Get-WmiObject -Class Win32_LogicalDisk -Filter \"DeviceID=\'C:\'\").Size/1GB\n\n# Get the total disk space in Go\n$totalSpace = (Get-WmiObject -Class Win32_LogicalDisk -Filter \"DeviceID=\'C:\'\").Size/1GB\n\n# Cr√©er un objet personnalis√© pour stocker les informations d\'inventaire\n$inventory = [PSCustomObject]@{\n    \"ComputerName\" = $computerName\n    \"IPAddress\" =  $ipAddress\n    \"InstalledSoftware\" =  $installedSoftware\n    \"CPU\" = $cpu\n    \"Memory\" = $memory\n    \"UsedSpace\" = \"{0:N2}\" -f $usedSpace\n    \"TotalSpace\" = \"{0:N2}\" -f $totalSpace\n}\n\n# Construire le chemin du fichier CSV\n$filePath = \"C:\\Inventory\\\" + $userName + \"_Inventory.csv\"\n\n# Enregistrer les informations d\'inventaire dans un fichier CSV\n$inventory | Export-Csv -Path $filePath -NoTypeInformation\n<e>[/code]</e></CODE></r>','04a8c79b9cf27b8e0747a7289490b72b',0,'AIA=','1d41bnke',1,0,'',0,0,0,1,0,'',0),
